import{_ as i,o as a,c as e,ai as t}from"./chunks/framework.B8DQqJnJ.js";const g=JSON.parse('{"title":"8. Настройка динамической трансляции адресов маршрутизаторах HQRTR и BR-RTR:","description":"","frontmatter":{},"headers":[],"relativePath":"module1/task1-8.md","filePath":"module1/task1-8.md"}'),l={name:"module1/task1-8.md"};function n(p,s,r,h,d,o){return a(),e("div",null,[...s[0]||(s[0]=[t('<h1 id="_8-настроика-динамическои-трансляции-адресов-маршрутизаторах-hqrtr-и-br-rtr" tabindex="-1">8. Настройка динамической трансляции адресов маршрутизаторах HQRTR и BR-RTR: <a class="header-anchor" href="#_8-настроика-динамическои-трансляции-адресов-маршрутизаторах-hqrtr-и-br-rtr" aria-label="Permalink to “8. Настройка динамической трансляции адресов маршрутизаторах HQRTR и BR-RTR:”">​</a></h1><p>• Настройте динамическую трансляцию адресов для обоих офисов в сторону ISP, все устройства в офисах должны иметь доступ к сети Интернет</p><p>Устанавливаем iptables</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>apt-get install iptables</span></span></code></pre></div><p>Ставим его на автозагрузку через</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>systemctl enable iptables</span></span></code></pre></div><p>Настраиваем iptables</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>iptables -t nat -A POSTROUTING -o &quot;интерфейс, который направлен в интернет, обычно ens18&quot; -j MASQUERADE</span></span></code></pre></div><p>Сохраняем настройки</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>iptables-save &gt;&gt; /etc/sysconfig/iptables</span></span></code></pre></div><p>Перезапускаем демон iptables, чтоб применить настройки</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>systemctl restart iptables.</span></span></code></pre></div>',12)])])}const k=i(l,[["render",n]]);export{g as __pageData,k as default};
