import{_ as a,o as e,c as i,ai as t}from"./chunks/framework.B8DQqJnJ.js";const k=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"module2/task2-2.md","filePath":"module2/task2-2.md"}'),l={name:"module2/task2-2.md"};function d(p,s,n,h,r,c){return e(),i("div",null,[...s[0]||(s[0]=[t('<h2 id="_2-сконфигурируите-фаиловое-хранилище-на-сервере-hq-srv" tabindex="-1">2. Сконфигурируйте файловое хранилище на сервере HQ-SRV: <a class="header-anchor" href="#_2-сконфигурируите-фаиловое-хранилище-на-сервере-hq-srv" aria-label="Permalink to “2. Сконфигурируйте файловое хранилище на сервере HQ-SRV:”">​</a></h2><details class="details custom-block"><summary>Задания</summary><p>• При помощи двух подключенных к серверу дополнительных дисков размером 1 Гб сконфигурируйте дисковый массив уровня 0</p><p>• Имя устройства – md0, при необходимости конфигурация массива размещается в файле /etc/mdadm.conf</p><p>• Создайте раздел, отформатируйте раздел, в качестве файловой системы используйте ext4</p><p>• Обеспечьте автоматическое монтирование в папку /raid</p></details><h3 id="_0-немного-советов" tabindex="-1">0. Немного советов <a class="header-anchor" href="#_0-немного-советов" aria-label="Permalink to “0. Немного советов”">​</a></h3><p>lsblk(расшифровывается как «list block devices» — «список блочных устройств») - выводит диски в системе и их точки монтирования</p><p>Прописав команду mount -a, можно проверить правильно ли написан fstab</p><h3 id="_1-конфиигурация-фаиловое-хранилище" tabindex="-1">1. Конфиигурация файловое хранилище <a class="header-anchor" href="#_1-конфиигурация-фаиловое-хранилище" aria-label="Permalink to “1. Конфиигурация файловое хранилище”">​</a></h3><p>Устанавливаем mdadm (от англ. multiple devices driver)</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>apt-get install –y mdadm</span></span></code></pre></div><p>Создаём массив</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>mdadm --create /dev/md0 –l 0 –n 2 /dev/sdb /dev/sdc</span></span></code></pre></div><p>Где:</p><p>/dev/md0 - название для логического девайса рейда</p><p>-l 0 – уровень RAID;</p><p>-n 2 – количество дисков, из которых собирается массив;</p><p>/dev/sdb /dev/sdc - диски для рейда</p><p>Форматируем раздел</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>mkfs.ext4 /dev/md0</span></span></code></pre></div><p>Создаём директорию</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>mkdir /raid</span></span></code></pre></div><p>Монтируем рейд в директорию /raid</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>mount /dev/md0  /raid</span></span></code></pre></div><p>Редакируем файл /etc/fstab, добовляя следуйщие строчки:</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>/dev/md0    /raid    ext4    defaults    0    0</span></span></code></pre></div>',23)])])}const g=a(l,[["render",d]]);export{k as __pageData,g as default};
