import{_ as a,o as e,c as i,ai as n}from"./chunks/framework.B8DQqJnJ.js";const g=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"other/internet_guide.md","filePath":"other/internet_guide.md"}'),t={name:"other/internet_guide.md"};function p(l,s,h,r,d,o){return e(),i("div",null,[...s[0]||(s[0]=[n(`<p>Ультимативный гайд на настройку интернета/связи между виртальными машинами</p><h2 id="_2-1-создание-и-настроика-интерфеисов" tabindex="-1">2.1 Создание и настройка интерфейсов <a class="header-anchor" href="#_2-1-создание-и-настроика-интерфеисов" aria-label="Permalink to “2.1 Создание и настройка интерфейсов”">​</a></h2><p>В директории /etc/net/ifaces создаем(mkdir &quot;имя интерфейса&quot;) или копируем(cp ens18 &quot;имя нового интерфейса&quot;, если уже существует интерфейс) По итогу на машине IPS должно получиться 3 интерфейса: ens18 ens19 ens20</p><p>Переходим в созданную директорию, создаем файлы(если их нет):</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>touch options ipv4address ipv4route resolv.conf</span></span></code></pre></div><h3 id="_2-2-настроика-динамеческого-dhcp-адреса" tabindex="-1">2.2 Настройка динамеческого(DHCP) адреса <a class="header-anchor" href="#_2-2-настроика-динамеческого-dhcp-адреса" aria-label="Permalink to “2.2 Настройка динамеческого(DHCP) адреса”">​</a></h3><p>Этим способом настраиваетсья только ens18(интерфейс в сторону интернета) на машине IPS</p><p>Редактируем файл options следуйшим образом:</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>BOOTPROTO=dhcp</span></span>
<span class="line"><span>TYPE=eth</span></span>
<span class="line"><span>CONFIG_IPV4=yes</span></span>
<span class="line"><span>CONFIG_WIRELESS=no</span></span>
<span class="line"><span>DISABLED=no</span></span></code></pre></div><p>Больше ничего настраивать не нужно</p><h3 id="_2-3-настроика-статического-адреса" tabindex="-1">2.3 Настройка статического адреса <a class="header-anchor" href="#_2-3-настроика-статического-адреса" aria-label="Permalink to “2.3 Настройка статического адреса”">​</a></h3><p>Этим способом настриваетсья все остольные машины</p><p>Начинаем настраивать файлы следуйщим образом:</p><p>Файл options</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>BOOTPROTO=static</span></span>
<span class="line"><span>TYPE=eth</span></span>
<span class="line"><span>CONFIG_IPV4=yes</span></span>
<span class="line"><span>CONFIG_WIRELESS=no</span></span>
<span class="line"><span>DISABLED=no</span></span></code></pre></div><p>Файл ipv4address</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>ip адрес интерфейса + префикс(ака сокрашенная маска),</span></span></code></pre></div><p>например 172.16.1.1/28 для HQ-RTR и 172.16.2.1/28 для BR-RTR</p><p>Файл ipv4route</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>ip адрес шлюза по умолчанию, куда должен вести интерфейс (маску писать не надо),</span></span></code></pre></div><p>Файл resolv.conf</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span>nameserver 8.8.8.8</span></span></code></pre></div><p>(Существуют и другие dns сервера, например nameserver 77.88.7.7, если хотите можете вписать ниже через enter)</p>`,23)])])}const k=a(t,[["render",p]]);export{g as __pageData,k as default};
